import React from 'react';
import Food from './components/images';


class App extends Component {
	initialState = {
		totalPoints: 0,
		gameOver: false,
		winner: false
	};

	state = {
		...this.initialState
  }
  handleClick = (nameOfFood, points) => {
		const addCoins = this.state.totalCandy + howMuchDoesItCost;
		const subCoins = this.state.totalCandy - howMuchDoesItCost;
		this.setState({
			totalCandy: this.state[nameOfWhateverIJustClickedOn] ? subCoins : addCoins,
			[nameOfWhateverIJustClickedOn]: !this.state[nameOfWhateverIJustClickedOn]
		});
	}
	resetGame = () => {
		this.setState(this.initialState);
	}
	render() {
		const Food = [
			{
				name: 'apple',
				amount: 12
			},
			{
				name: 'burger',
				amount: 8
			},
			{
				name: 'fries',
				amount: 2
			},
		];
	}
}
export default App;
